import {useParams, useNavigate} from 'react-router-dom';
import {useEffect, useState} from 'react';
import axios from 'axios';

function SocketForm({isNew = false}) {
    const {id} = useParams(); // gets :id from URL
    const navigate = useNavigate();

    const [socket, setSocket] = useState({
        name: '',
        type: '',
        brand: '',
        description: ''
    });


    useEffect(() => {
        axios.get('http://localhost:8080/api/sockets').then(res => setSockets(res.data));

        if (!isNew && id) {
            axios.get(`http://localhost:8080/api/cpus/${id}`).then(res => setCpu(res.data));
        }
    }, [id, isNew]);

    const handleChange = e => {
        const {name, value} = e.target;
        if (name === 'socket') {
            setCpu({...socket, socket: {id: value}});
        } else {
            setCpu({...socket, [name]: value});
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const cpuData = isNew ? {...socket} : socket;

        if (!socket.name) {
            alert('Please fill in all required fields.');
            return;
        }

        const method = isNew ? 'post' : 'put';
        const url = isNew ? '/api/cpus' : `/api/cpus/${id}`;
        axios[method](`http://localhost:8080${url}`, cpuData)
            .then(() => navigate('/'));
    };

    return (
        <div className="container">
            <h1>{isNew ? 'Add CPU' : 'Edit CPU'}</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Name</label>
                    <input name="name" value={socket.name || ''} onChange={handleChange} placeholder="name"/>
                </div>

                <div>
                    <label>Brand</label>
                    <input name="brand" value={socket.brand || ''} onChange={handleChange} placeholder="Brand"/>
                </div>
                <div>
                    <label>Type</label>
                    <input name="type" value={socket.type || ''} onChange={handleChange}
                           placeholder="type" />
                </div>

                <button type="submit">{isNew ? 'Add CPU' : 'Save Changes'}</button>
            </form>
        </div>
    );
}

export default SocketForm;
